:root {
    --primary-color: #8C40F0;
    --secondary-color: #FF8C40;
    --dark-color: #000000;
}

body {
    background-color: #f8f9fa;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
    margin: 0;
}

.sidebar {
    position: fixed;
    top: 0;
    left: 0;
    height: 100vh;
    background: white;
    box-shadow: 2px 0 10px rgba(0,0,0,0.1);
    transition: all 0.3s ease;
    z-index: 1050;
    width: 250px;
}

.sidebar.collapsed {
    width: 70px;
}

.sidebar-header {
    padding: 1rem;
    border-bottom: 1px solid #dee2e6;
    display: flex;
    align-items: center;
    justify-content: space-between;
    height: 70px;
}

.sidebar-header .app-version {
    font-size: 0.75rem;
    color: #aaa;
    margin-left: 8px;
    font-weight: 500;
}

.sidebar.collapsed .app-version {
    display: none;
}
.sidebar:not(.collapsed) .app-version {
    display: inline;
}

.logo-full {
    height: 75px;
    transition: opacity 0.3s ease;
}

.logo-mini {
    width: 30px;
    height: 30px;
    display: none;
    align-items: center;
    justify-content: center;
}

.sidebar.collapsed .logo-full {
    display: none;
}

.sidebar.collapsed .logo-mini {
    display: flex;
}

.toggle-btn {
    background: none;
    border: none;
    color: var(--primary-color);
    font-size: 18px;
    padding: 2px;
    border-radius: 6px;
    transition: all 0.3s ease;
}

.toggle-btn:hover {
    background-color: #f8f9fa;
}

.sidebar-menu {
    padding: 1rem 0;
    height: calc(100vh - 140px);
    overflow-y: auto;
}

.menu-item {
    margin-bottom: 4px;
    font-size: 0.9rem;
}

.menu-link {
    display: flex;
    align-items: center;
    padding: 12px 16px;
    color: #374151;
    text-decoration: none;
    transition: all 0.3s ease;
    border-left: 3px solid transparent;
    position: relative;
}

.menu-link:hover {
    background-color: #f8f9fa;
    color: var(--primary-color);
}

.menu-link.active {
    background-color: rgba(140, 64, 240, 0.1);
    color: var(--primary-color);
    border-left-color: var(--primary-color);
}

.menu-icon {
    font-size: 18px;
    width: 24px;
    text-align: center;
}

.menu-text {
    margin-left: 12px;
    font-weight: 500;
    transition: opacity 0.3s ease;
}

.menu-arrow {
    margin-left: auto;
    transition: transform 0.3s ease;
}

.menu-arrow.rotated {
    transform: rotate(180deg);
}

.sidebar.collapsed .menu-text,
.sidebar.collapsed .menu-arrow {
    opacity: 0;
    width: 0;
    margin: 0;
}

.submenu {
    background-color: #f8f9fa;
    border-left: 2px solid var(--primary-color);
    margin-left: 20px;
    display: none;
}

.submenu.show {
    display: block;
}

.sidebar.collapsed .submenu {
    display: none !important;
}

.submenu-link {
    display: flex;
    align-items: center;
    padding: 8px 24px;
    color: #6b7280;
    text-decoration: none;
    font-size: 0.8rem;
    transition: all 0.3s ease;
}

.submenu-link:hover {
    background-color: white;
    color: #374151;
}

.submenu-icon {
    font-size: 14px;
    width: 16px;
    margin-right: 8px;
}

.user-section {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    border-top: 1px solid #dee2e6;
    background: white;
}

.user-profile {
    display: flex;
    align-items: center;
    padding: 16px;
    cursor: pointer;
    transition: background-color 0.3s ease;
}

.user-profile:hover {
    background-color: #f8f9fa;
}

.user-initial {
    width: 30px;
    height: 30px;
    border-radius: 50%;
    background: white;
    color: var(--primary-color);

.card-header {
    background: linear-gradient(135deg, #8C40F0 0%, #FF8C40 100%);
    color: white;
}
.nav-tabs .nav-link {
    background-color: #ffffff;
    color: #b9b9b9;
    border-color: #ebebeb;
}

.nav-tabs .nav-link.active {
    background: linear-gradient(150deg, #8C40F0 0%, #FF8C40 100%);
    color: #ffffff;
    border-color: #ebebeb;
}

    .nav-tabs .nav-link:disabled {
    background-color: #ffffff;
    color: #b9b9b9;
    border-color: #ebebeb;
}

.card-metric { border-left-width: 4px; }
.border-success-subtle { border-color: #d1e7dd !important; }
.border-danger-subtle { border-color: #f8d7da !important; }
.border-primary-subtle { border-color: #cfe2ff !important; }
.border-warning-subtle { border-color: #fff3cd !important; }

.tab-content > .tab-pane {
    display: none;
}
.tab-content > .active {
    display: block;
}

