    background: white;
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
    box-shadow: 0 20px 60px rgba(0,0,0,0.3);
    overflow: hidden;
}

.header {
    max-width: 1600px;
    margin: 0 auto;
    background: linear-gradient(135deg, #4e6ae9 0%, #764ba2 100%);
    color: white;
    padding: 7px;
    text-align: left;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
}

.header h4 {
    font-size: 24px;
    font-weight: 600;
    margin: 5px;
}

.content-wrapper {
    padding: 10px;
}

form {
    display: flex;
    gap: 20px;
    flex-wrap: wrap;
}

/* Empresa + Período → 2/3 */
.filters-section:first-of-type {
    flex: 2;
}

/* Alíquotas → 1/3 */
.filters-section:last-of-type {
    flex: 1;
}

.filters-section {
    min-width: 300px;
    display: flex;
    align-items: center;
    gap: 10px;
    flex-wrap: wrap;
    padding-bottom: 20px;
}

.filter-group {
    display: flex;
    flex-direction: column;
    max-width: 400px;
}

.filter-group label {
    font-weight: 600;
    margin-bottom: 8px;
    color: #2c3e50;
    font-size: 13px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.filter-group select,
.filter-group input {
    padding: 12px 15px;
    border: 2px solid #e0e0e0;
    border-radius: 8px;
    font-size: 14px;
    transition: all 0.3s ease;
    background: white;
}

.filter-group-aliquota select,
.filter-group-aliquota input {
    max-width: 115px;
}

.filter-group select:focus,
.filter-group input:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
}

.btn-filtrar {
    grid-column: 1 / -1;
    padding: 11px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border: none;
    border-radius: 8px;
    font-size: 16px;
    font-weight: 500;
    cursor: pointer;
    transition: transform 0.2s ease, box-shadow 0.2s ease;
    text-transform: uppercase;
    letter-spacing: 1px;
    margin-top: 25px;
}

.btn-filtrar:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 20px rgba(102, 126, 234, 0.3);
}

.btn-filtrar:active {
    transform: translateY(0);
}

/* Tabs */
.tabs-container {
    margin-bottom: 30px;
}

.tabs-header {
    display: flex;
    gap: 5px;
    border-bottom: 2px solid #e0e0e0;
    margin-bottom: 15px;
}

.tab-button {
    padding: 15px 30px;
    background: transparent;
    border: none;
    border-bottom: 3px solid transparent;
    color: #6c757d;
    font-size: 15px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    position: relative;
}

.tab-button:hover {
    color: #667eea;
    background: #f8f9fa;
}

.tab-button.active {
    color: #667eea;
    border-bottom-color: #667eea;
    background: white;
}

.tab-content {
    display: none;
    animation: fadeIn 0.3s ease;
}

.tab-content.active {
    display: block;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(10px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

