.menu-link {
    display: flex;
    align-items: center;
    padding: 12px 16px;
    color: #374151;
    text-decoration: none;
    transition: all 0.3s ease;
    border-left: 3px solid transparent;
    position: relative;
}

.menu-link:hover {
    background-color: #f8f9fa;
    color: var(--primary-color);
}

.menu-link.active {
    background-color: rgba(140, 64, 240, 0.1);
    color: var(--primary-color);
    border-left-color: var(--primary-color);
}

.menu-icon {
    font-size: 18px;
    width: 24px;
    text-align: center;
}

.menu-text {
    margin-left: 12px;
    font-weight: 500;
    transition: opacity 0.3s ease;
}

.menu-arrow {
    margin-left: auto;
    transition: transform 0.3s ease;
}

.menu-arrow.rotated {
    transform: rotate(180deg);
}

.sidebar.collapsed .menu-text,
.sidebar.collapsed .menu-arrow {
    opacity: 0;
    width: 0;
    margin: 0;
}

.submenu {
    background-color: #f8f9fa;
    border-left: 2px solid var(--primary-color);
    margin-left: 20px;
    display: none;
}

.submenu.show {
    display: block;
}

.sidebar.collapsed .submenu {
    display: none !important;
}

.submenu-link {
    display: flex;
    align-items: center;
    padding: 8px 24px;
    color: #6b7280;
    text-decoration: none;
    font-size: 0.8rem;
    transition: all 0.3s ease;
}

.submenu-link:hover {
    background-color: white;
    color: #374151;
}

.submenu-icon {
    font-size: 14px;
    width: 16px;
    margin-right: 8px;
}

.user-section {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    border-top: 1px solid #dee2e6;
    background: white;
}

.user-profile {
    display: flex;
    align-items: center;
    padding: 16px;
    cursor: pointer;
    transition: background-color 0.3s ease;
}

.user-profile:hover {
    background-color: #f8f9fa;
}

.user-initial {
    width: 30px;
    height: 30px;
    border-radius: 50%;
    background: white;
    color: var(--primary-color);

