body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    color: #ffffff;
    min-height: 100vh;
    padding: 1px;
}

.container {
    max-width: 1600px;
    margin: 0 auto;
}

.header {
    background: linear-gradient(135deg, #8C40F0 0%, #FF8C40 100%);
    padding: 15px;
    border-radius: 15px;
    margin-bottom: 15px;
    box-shadow: 0 10px 30px rgba(140, 64, 240, 0.3);
}

.actions-bar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
    gap: 15px;
    flex-wrap: wrap;
}

.btn {
    padding: 12px 25px;
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-size: 1em;
    font-weight: 600;
    transition: all 0.3s ease;
    display: inline-flex;
    align-items: center;
    gap: 8px;
    text-decoration: none;
}

.btn-primary {
    background: linear-gradient(135deg, #8C40F0 0%, #a860ff 100%);
    color: white;
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 20px rgba(140, 64, 240, 0.4);
}

.btn-success {
    background: linear-gradient(135deg, #FF8C40 0%, #ffaa66 100%);
    color: white;
}

.btn-success:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 20px rgba(255, 140, 64, 0.4);
}

.btn-danger {
    background: linear-gradient(135deg, #ff4444 0%, #ff6666 100%);
    color: white;
    font-size: 0.9em;
    padding: 8px 15px;
}

.btn-danger:hover {
    transform: scale(1.05);
    box-shadow: 0 3px 15px rgba(255, 68, 68, 0.4);
}

.btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.stats-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 15px;
    margin-bottom: 20px;
}

.stat-card {
    background: rgba(140, 64, 240, 0.1);
    border: 2px solid #8C40F0;
    padding: 20px;
    border-radius: 10px;
    text-align: center;
}

.stat-number {
    font-size: 2em;
    font-weight: bold;
    color: #FF8C40;
}

.stat-label {
    font-size: 0.9em;
    opacity: 0.8;
    margin-top: 5px;
}

.table-container {
    background: rgba(255, 255, 255, 0.05);
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.5);
    height: 80vh;
    overflow-y: auto;
}

table {
    width: 100%;
    border-collapse: collapse;
}

thead {
    background: linear-gradient(135deg, #8C40F0 0%, #a860ff 100%);
    top: 0;
    z-index: 2;
    position: sticky;
}

th {
    padding: 18px 15px;
    text-align: left;
    font-weight: 600;
    font-size: 1em;
    position: sticky;
    user-select: none;
    top: 0;
    z-index: 2;
}

.th-content {
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 10px;
}

.filter-icon {
    cursor: pointer;
    opacity: 0.7;
    transition: all 0.3s ease;
    font-size: 1.1em;
}

.filter-icon:hover {
    opacity: 1;
    color: #FF8C40;
    transform: scale(1.2);
}

.filter-icon.active {
    color: #FF8C40;
    opacity: 1;
}

tbody tr {
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    transition: all 0.3s ease;
}

tbody tr:hover {
    background: rgba(140, 64, 240, 0.15);
    transform: scale(1.001);
}

td {
    padding: 5px;
    font-size: 0.95em;
    color : black;
    text-align: left;
    border: #cfcfcf 0.5px solid;
}

